

class GarageDoor:
    """ GarageDoor vendor class """

    def up(self):
        """ Garage door goes up"""
        print('Garage door goes UP')

    def down(self):
        """ Garage door goes down """
        print('Garage door goes DOWN')

    def stop(self):
        """ Stops the garage door """
        print('Garage door movement STOP')

    def light_on(self):
        """ Turns light on """
        print('Garage door light ON')

    def light_off(self):
        """ Turns light off """
        print('Garage door light OFF')
